package com.demo.ArrayHandler;

public class DynamicArrayCreation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String[] numArray =new String[4];
		
		numArray[0]="Mahesh";
		numArray[1]="Mahi";
		numArray[2]="Mahendhar";
		numArray[3]="Manoj";
		
		for(String val:numArray)
		{
			System.out.print(val+" ");
		}
		
		
	}

}
