package com.examplelogics;

public class DowhileDemo
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		
	 int i=5;
		 
		 do
		 {
			 System.out.println(i);
			 i++;
		 }
		 while(i<=2);
		
		System.out.println(" Another Eample of dowhile");
		
		 int c=6;
		 
		 do {
			 System.out.println(c);
			 c++;
		 }
		 while(c<=8);
		 

	}

}
