package com.examplelogics;

public class SwichDemo {

	public static void main(String[] args)
	
	{
		// TODO Auto-generated method stub
		
		 String name = "mah";
		 
		  switch(name){
			  case  "mahi" :
			  {
				  System.out.println("Name :" +name );
				  break;
			  }
			  
			  case "Mahesh" :
			  {
				System.out.println("Name :"+ name); 
				break;
			  }
			  case "vinay" :
			  {
				System.out.println("Name :"+ name);  
			  }
			  
			  case "mahesh" :
			  {
				System.out.println("Name :"+ name);
				break;
			  }
			  case "Mahendhar" :
			  {
				System.out.println("Name :"+ name);  
			  }
			 
			  case "mah" :
			  {
				  
				System.out.println("Name :"+ name);  
				break;
			  }
			  
			  case "Sai" :
			  {
				  System.out.println("Name "+name);
				   break;
			  }
			 
			 default :
			  {
				   System.out.println(" Default Name"+ name);
				   break;
			  }
			  
				  
		  }
		  
		 

	}

}
